pragma solidity 0.8.17;

import {Buffer} from "./Buffer.sol";
import {BufferBase} from "./BufferBase.sol";

library RequestLib {
  using Buffer for BufferBase.buffer;

  struct Request {
    bytes32 id;
    address callbackAddress;
    bytes4 callbackFunctionId;
    BufferBase.buffer requestData;
  }

  function initialize(
    Request memory self,
    address callbackAddr,
    bytes4 callbackFunc
  ) internal pure returns (Request memory) {
    self.callbackAddress = callbackAddr;
    self.callbackFunctionId = callbackFunc;
    return self;
  }

  function setId(Request memory self, bytes32 id) internal pure {
    self.id = id;
  }

  function add(Request memory self, string memory key, string memory value) internal pure {
    self.requestData.encodeString(key);
    self.requestData.encodeString(value);
  }

  function addHeader(Request memory self, string memory headerKey, string memory value) internal pure {
    self.requestData.encodeString(" header(");
    self.requestData.encodeString(headerKey);
    self.requestData.encodeString(value);
    self.requestData.encodeString(") ");
  }
}","keccak256":"0xf8f70cb860bdc2202257a50ef25d755d31ccebcf9ffb900f8df3e9045831fd53","license":"UNLICENSED"},"contracts/pull/HttpRequestOracle.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../lib/RequestLib.sol";

error OnlyReporter();

contract HttpRequestOracle is Ownable {
  using RequestLib for RequestLib.Request;

  mapping(address => bool) reporters;

  event RequestSent(bytes32 indexed id, bytes data);
  event RequestFulfilled(bytes32 indexed id);
  event RequestCancelled(bytes32 indexed id);

  function updateReporter(address reporterAddress, bool isReporter) external onlyOwner {
    reporters[reporterAddress] = isReporter;
  }

  function getReporter(address reporterAddress) external view returns (bool) {
    return reporters[reporterAddress];
  }

  function _hasReporter(address reporterAddress) private view returns (bool) {
    return reporters[reporterAddress];
  }

  function sendRequest(RequestLib.Request memory req) external returns (bytes32) {
    bytes memory encodedRequest = abi.encodeWithSelector(
      this.updateReporter.selector,
      req.id,
      req.callbackFunctionId,
      req.requestData.buf
    );
    emit RequestSent(req.id, encodedRequest);
    return req.id;
  }
}","keccak256":"0xc69e4f20d24c112f1a3effb2b7c0b0af033f172d94ced2149c73195e9e2f7d04","license":"UNLICENSED"}},"version":1}
